import React from 'react'
import { Col, Row } from 'react-bootstrap'

import dataAccess from '../data/dataAccess'
import '../style/AboutTheCouple.css';
import AboutSection from './AboutSection'

class AboutTheCouple extends React.Component {
    constructor(props){
      super(props)
      this.state = {
        sheetLoaded: false,
        aboutValues: {}
      }
    }

    componentDidMount() {
      if(this.state.sheetLoaded){
        return;
      }
      var sheetData = dataAccess.googleSheets('sheet1').then((data) =>{
        this.setState({
          sheetLoaded: true,
          aboutValues: data
        })
      })
    }

    render(){

      // create the aboutSections based on what's in the sheet
      // let aboutSections = this.state.sheetLoaded ?
      //   Object.keys(this.state.aboutValues).map((key, index) => <AboutSection key={index} title={this.state.aboutValues[key][1]} desc={this.state.aboutValues[key][0]} />)
      //   : null

      return (
        <div id="about-the-couple">
          {
            !this.state.sheetLoaded ? null :
              <div data-aos="fade-right">
              <AboutSection title={this.state.aboutValues["AboutSara"][1]} desc={this.state.aboutValues["AboutSara"][0]} />
              </div>
          }
          {/*<h1>About The Couple</h1>
          <Row>
            {aboutSections}
          </Row>*/}

          <div data-aos="fade-up" style={{height: "500px", width: "500px", backgroundColor: "orange"}}></div>
        </div>
      )
    }
}

export default AboutTheCouple
