import React from 'react'
import { Col, Row } from 'react-bootstrap'
import GoogleMapReact from 'google-map-react';

import dataAccess from '../data/dataAccess'
import '../style/Bethlehem.css';
import AboutSection from './AboutSection'
import MapMarker from './MapMarker'
import api from '../data/apiKey'

class AboutBethlehem extends React.Component {
    constructor(props){
      super(props)
      this.state = {
        sheetLoaded: false,
        aboutValues: []
      }
    }

    componentDidMount() {
      if(this.state.sheetLoaded){
        return;
      }
      var sheetData = dataAccess.googleSheets('sheet2').then((data) =>{
        this.setState({
          sheetLoaded: true,
          aboutValues: data
        })
      })
    }

    stateDataCallback = (valueObjects, data) => {
      var markerDataArray = []
      data.valueRanges[0].values.forEach((value) => {
        markerDataArray.push({
          lat: value[0],
          lng: value[1],
          text: value[2]
        })
      })
      return markerDataArray
    }

    mapsDefaultInfo = {
      center: {
        lat: 40.6259,
        lng: -75.3705
      },
      zoom: 13
    };

    render(){

      // create the aboutSections based on what's in the sheet
      let aboutSections = this.state.sheetLoaded ?
        Object.keys(this.state.aboutValues).map((key, index) => <AboutSection key={index} title={this.state.aboutValues[key][1]} desc={this.state.aboutValues[key][0]} />)
        : null

      return (
        <div id="about-bethlehem">
          <div id="map-container">
            <GoogleMapReact
              defaultCenter={this.mapsDefaultInfo.center}
              defaultZoom={this.mapsDefaultInfo.zoom}
              bootstrapURLKeys={{
                key: api.mapsApiKey,
              }}
            >
              <MapMarker
                lat={40.6153}
                lng={-75.3682}
                text="Reception Venue" />
              <MapMarker
                lat={40.6200}
                lng={-75.3825}
                text="Hotel B" />
              <MapMarker
                lat={40.6347}
                lng={-75.3664}
                text="St. Anne's Church" />
            </GoogleMapReact>
      </div>
        </div>
      )
    }
}

export default AboutBethlehem
